import React, { useEffect, useState } from 'react'
import Chart from 'react-apexcharts';

import { barOptions, pieOptions } from './chart-options';
import axios from 'axios';

import Filters from '../../components/filters/filters';
import { buildBarSeries } from './helpers'
import './styles.css'

type PieChartData = {
    labels: string[];
    series: number[];
}

type BarChartData = {
    x: string;
    y: number;
}

const initialPieData = {
    labels: [],
    series: []
}

const BASE_URL = 'http://localhost:8080'

const Charts = () => {
    const [barChatData, setBatChartData] = useState<BarChartData[]>([]);
    const [platformData, setPlaformData] = useState<PieChartData>(initialPieData);
    const [genderData, setGenderData] = useState<PieChartData>(initialPieData);

    useEffect(() => {
        async function getData() {
            const recordsResponse = await axios.get(`${BASE_URL}/records`);
            const gamesResponse = await axios.get(`${BASE_URL}/games`);

            const barData = buildBarSeries(gamesResponse.data, recordsResponse.data)
        }
        getData();
    }, [])

    return (
        <div className="page-container">
            <Filters link="/records" linkText="VER TABELA" />
            <div className="chart-container">
                <div className="top-related">
                    <h1 className="top-related-title">
                        Jogos mais votados
                    </h1>
                    <div className="games-container">
                        <Chart
                            options={barOptions}
                            type="bar"
                            width="900"
                            height="150"
                            series={[{ data: barChatData }]} />
                    </div>
                </div>
                <div className="charts">
                    <div className="platform-chart">
                        <h2 className="chart-title">Plataformas</h2>
                        <Chart
                            options={{ ...pieOptions, labels: platformData?.series }}
                            type="donut"
                            series={platformData?.series}
                            width="650"
                        />
                    </div>
                    <div className="gender-chart">
                        <h2 className="chart-title">Gêneros</h2>
                        <Chart
                            options={{ ...pieOptions, labels: genderData?.labels }}
                            type="donut"
                            series={genderData?.labels}
                            width="650"
                        />
                    </div>
                </div>
            </div>
        </div>
    )
};

export default Charts;

